mapper.select:
SELECT M_ID, M_NAME, M_NICKNAME,M_EMAIL,M_PHONENUMBER
FROM MEMBER
WHERE M_ID = #{memberid}

mapper.update:
UPDATE QUESTION
SET QUESTION.Q_STATUS='Y',
QUESTION.Q_A_NO = #{a_no}
WHERE QUESTION.Q_NO = #{q_no}

UPDATE NOTICE
SET N_TITLE = #{n_title},
N_CONTENT = #{n_content},
N_PATH = #{n_path}
WHERE N_NO = #{n_no}

UPDATE ANSWER
SET A_TITLE = #{a_title},
A_CONTENT = #{a_content}
WHERE A_NO = #{a_no}

UPDATE NOTICE 
SET N_READCOUNT=#{n_readcount}+1
WHERE N_NO=#{n_no}

UPDATE NOTICE
SET N_STATUS = 'N'
WHERE N_NO=#{no}
