mapper.insert:
INSERT INTO MEMBER_PARTY(
	P_NO,
	M_NO,
	STATUS
)
VALUES(
	#{p_no},
	#{m_no},
	#{m_status}
)

INSERT INTO POINT(
	PO_NO,
	PO_POINT,
	M_NO
)
VALUES(
	SEQ_POINT_NO.NEXTVAL,
	#{po_point},
	#{m_no}
)

mapper.update:
UPDATE PARTY
SET
P_SHARE_ID=#{p_share_id},
P_SHARE_PWD=#{p_share_pwd}
WHERE P_NO=#{p_no}
