mapper.update:
UPDATE  REVIEW_CMT
SET 
		CMT_UPDATE = SYSDATE,


	  	CMT_CONTENT = #{cmtContent}


		CMT_STATUS = #{cmtStatus}


WHERE 	CMT_NO = #{cmtNo}

UPDATE  REVIEW_CMT
SET 
		CMT_UPDATE = SYSDATE,


	  	CMT_CONTENT = #{cmtContent}


		CMT_STATUS = #{cmtStatus}


WHERE 	CMT_NO = #{cmtNo} AND 
		CMT_DEPTH = #{cmtDepth}
